color_t *vectorToMatrix(vector_t *vector){

	static color_t temp[32][32];
	for(uint8_t i = 0; i < 32; i++) for(uint8_t j = 0; j < 32; j++) temp[i][j] = color[i][j];

	for(uint16_t i = 0; i < vector->taille; i++){
		if(getPixel(vector, i)->visible == 1){
			if(getPixel(vector, i)->position_y < 16){
				if(getPixel(vector, i)->position_y == 0) color[15][getPixel(vector, i)->position_x] = getPixel(vector, i)->color;
				else color[getPixel(vector, i)->position_y - 1][getPixel(vector, i)->position_x] = getPixel(vector, i)->color;
			} else {
				if(getPixel(vector, i)->position_y == 16) color[31][getPixel(vector, i)->position_x] = getPixel(vector, i)->color;
				else color[getPixel(vector, i)->position_y - 1][getPixel(vector, i)->position_x] = getPixel(vector, i)->color;
			}
		}
	}

	return *temp;
}
